import axios from 'axios';
import {GET_ACTIVITIES, GET_BY_NAME, GET_COUNTRIES} from './constantes';



export function getCountries(){
    return async function(dispatch){
        return await axios.get('http://localhost:3001/countries')
        .then(resp => {
            dispatch({
                type: GET_COUNTRIES,
                payload: resp.data
            })
        })
    }
};


export function getActivities(){
    return async function(dispatch){
        return await axios.get('http://localhost:3001/activities')
        .then(resp => {
            dispatch({
                type: GET_ACTIVITIES,
                payload: resp.data
            })
        })
    }
};


export function getByName(name){
    return async function(dispatch){
        return await axios.get(`http://localhost:3001/countries?name=${name}`)
        .then(res => {
            dispatch({
                type: GET_BY_NAME,
                payload: res.data
            })
        }) 
    }    
};
