import { GET_COUNTRIES } from "../actions/constantes";

const initialState = {
allCountries: []
};



function rootReducer(state=initialState, action){
    switch(action.type){

        case GET_COUNTRIES:
            return {
                ...state,
                allCountries: action.payload
            }





        default: return state;
    }
};

export default rootReducer;
