import axios from "axios";
import { DELETE_ACT } from "./constantes";



export default function deleteAct(idAct){
    return async function(dispatch){     
        return await axios.post(`http://localhost:3001/activities/delete/${idAct}`)
        .then(resp => {
            dispatch({
                type: DELETE_ACT,
                payload: resp.data
            })
        })
    }    
};
