import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Link, useParams } from 'react-router-dom';
import { getByID } from '../actions/actions';
import ActivitiesDetail from './ActivitiesDetail';

export default function CountryDetail() {

const details = useSelector((state)=> state.detail);


const dispatch = useDispatch();

const {id} = useParams()
useEffect(()=>{
    dispatch(getByID(id));    
},[id]);
   


  return (
    <div>
        <img src={details.image} alt='Without flag'/>
        <h3>{details.id}</h3>
        <h3>{details.name}</h3>
        <h3>{details.continent}</h3>
        <h3>{details.capital}</h3>
        <h3>{details.subregion}</h3>
        <h3>{details.area}</h3>
        <h3>{details.population}</h3>
        <ActivitiesDetail actsDetail={details.activities}/>
        <Link to='/home'>
        <button>Go to Home</button>
        </Link>
    </div>
  )
};
