import { GET_COUNTRIES } from "../actions/constantes";

let initialState = {
allCountries: []
};



export default function rootReducer(state = initialState, action){
    switch(action.type){

        case GET_COUNTRIES:
            return {
                ...state,
                allCountries: action.payload
            }





        default: return state;
    }
};
