import React,{ useState, useEffect } from "react";
import ContainerCards from "../Containers/ContainerCards";
import { useSelector, useDispatch } from "react-redux";
import { getCountries } from "../actions/actions";

export default function Home(){

    const dispatch = useDispatch();

    const allCountries = useSelector((state)=> state.allCountries);
    
    const [currPage, setCurrPage] = useState(1);
    const [countriesPage, setCountriesPage] = useState(10);
    const lastCountry = currPage * countriesPage;
    const firstCountry = lastCountry - countriesPage;
    const currCountries = allCountries.slice(firstCountry, lastCountry);
    
    const paginatedNum = (pageNum)=>{
            setCurrPage(pageNum);
            if(currPage === 1) setCountriesPage(9);
        };

    useEffect(()=>{
            dispatch(getCountries());
        });






    return (
        <div>
            <h3> Este es el Home </h3>

            <div>
                <select>
                    <option>Tourist activity</option>
                    <option>Continent</option>
                </select>

                <select>Order by
                    <option>Order A-Z</option>
                    <option>Order Z-A</option>
                    <option>Order Max. pop. ▼</option>
                    <option>Order Min. pop. ▲</option>
                </select>

            <ContainerCards countries={currCountries}/>
















            </div>
        </div>
    )
};
