import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getActivities } from '../actions/actions';
import deleteAct from '../actions/deleteAct';
import CardActivity from './CardActivity';







export default function ListActivities() {

  const activities = useSelector((state)=> state.activities);  

  const dispatch = useDispatch();

  useEffect(()=>{
    dispatch(getActivities())
  });

  const countries = activities.map(act=>act.countries.map(c=>(
  <ul key={c.name}>
    <li>{c.name}</li>
    <img src={c.image} width='75' height='50' alt='Flag'/>
  </ul>
  )));
    
  function handleDeleteActivity(idAct){
    dispatch(deleteAct(idAct));
    alert('Deleted successfully');
  };

  return (
    <div>
      <h3>List Activities</h3>
    
    <div>
      {
        activities?.map(act => {
          return (
            <ul key={act.name}>
              <li>
                <div>
                  <button onClick={()=>handleDeleteActivity(act.id)}>X</button>
                  <CardActivity
                  name={act.name}
                  difficulty={act.difficulty}
                  duration={act.duration}
                  season={act.season}
                  countries={countries}
              />
                </div>
              </li>
            </ul>
          )
        })
      }
    </div>


    </div>
  )
};
