import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getActivities } from '../actions/actions';
import deleteAct from '../actions/deleteAct';
import CardActivity from './CardActivity';







export default function ListActivities() {

  const activities = useSelector((state)=> state.activities);  

  const dispatch = useDispatch();

  const [activs, setActivs] = useState(activities);

  useEffect(()=>{
    dispatch(getActivities())
  },[activs]);

    
  function handleDeleteActivity(idAct){
    dispatch(deleteAct(idAct));
    alert('Deleted successfully');
    setActivs(activities);
  };

  return (
    <div>
      <h3>List Activities</h3>
    
      <div>
      <table>
          <tbody>
            <tr>
              <th>Name</th>
              <th>Difficulty</th>
              <th>Duration</th>
              <th>Season</th>
              <th>Country</th>
              <th>Flag</th>

            </tr>
            {
              activities?.map(el => {
                const country = el.countries.map(e=> e.name);
                const flag = el.countries.map(flag=>flag.image);
                return (
                <tr key={el.id}>
                  <td>{el.name}</td>
                  <td>{el.difficulty}</td>
                  <td>{el.duration}</td>
                  <td>{el.season}</td>
                  <td>{country}</td>
                  <td><img src={flag} width='75' height='50' alt='Flag'/></td>
                </tr>
                )
              })
            }
          </tbody> 
        </table>
      </div>


    </div>
  )
};

