import React, { useState } from 'react'

export default function Form() {

    const [input, setInput] = useState({
        name: '',
        diffculty: '',
        duration: '',
        season: ''
    });

    function handleInputChange(e){
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
    };

    function handleSubmit(e){
        e.preventDefault();
    };






  return (
    <div>
        <form onSubmit={(e)=> handleSubmit(e)}>
            <input type='text' name='name' value={input.name} onChange={(e)=>handleInputChange(e)}></input>

            <input type='text' name='diffculty' value={input.diffculty} onChange={(e)=>handleInputChange(e)}></input>

            <input type='text' name='duration' value={input.duration} onChange={(e)=>handleInputChange(e)}></input>

            <input type='text' name='season' value={input.season} onChange={(e)=>handleInputChange(e)}></input>

            <button type='submit'>Submit</button>
        </form>
    </div>
  )
};
