import React, { useState } from 'react';


function validateForm(input){
    const error = {};
    if(!input.name) error.name = 'Name is required';
    
    return error;
};

export default function Form() {

    
    const [input, setInput] = useState({
        name: '',
        diffculty: '',
        duration: '',
        season: ''
    });
    
    const [error, setError] = useState({});

    function handleInputChange(e){
        setInput({
            ...input,
            [e.target.name]: e.target.value
        });

        setError(validateForm({
            ...input,
            [e.target.name]: e.target.value
        })
        )
    };

    function handleSubmit(e){
        e.preventDefault();
        setInput({
            name: '',
            diffculty: '',
            duration: '',
            season: ''
        })
    };






  return (
    <div>
        <form onSubmit={(e)=> handleSubmit(e)}>
            <>
            <label>Name </label>
            <input type='text' name='name' value={input.name} onChange={(e)=>handleInputChange(e)} required placeholder='Activity...'></input>
            {
                error.name && <p>{error.name}</p>
            }
            </>

            <label>Difficulty </label>
            <input type='text' name='diffculty' value={input.diffculty} onChange={(e)=>handleInputChange(e)}required></input>

            <label>Duration </label>
            <input type='text' name='duration' value={input.duration} onChange={(e)=>handleInputChange(e)} required></input>

            <label>Season </label>
            <input type='text' name='season' value={input.season} onChange={(e)=>handleInputChange(e)} required></input>

            <button type='submit'>Submit</button>
        </form>
    </div>
  )
};
