import React, { useState } from 'react';


function validateForm(input){
    const error = {};
    if(!input.name) error.name = 'Name is required';
    
    return error;
};

export default function Form() {

    const [error, setError] = useState({})

    const [input, setInput] = useState({
        name: '',
        diffculty: '',
        duration: '',
        season: ''
    });

    function handleInputChange(e){
        setInput({
            ...input,
            [e.target.name]: e.target.value
        });
        validateForm(setInput({
            ...input,
            [e.target.name]: e.target.value
        }))
    };

    function handleSubmit(e){
        e.preventDefault();
        setInput({
            name: '',
            diffculty: '',
            duration: '',
            season: ''
        })
    };






  return (
    <div>
        <form onSubmit={(e)=> handleSubmit(e)}>
            <>
            <p>Name</p>
            <input type='text' name='name' value={input.name} onChange={(e)=>handleInputChange(e)}></input>
            </>

            <input type='text' name='diffculty' value={input.diffculty} onChange={(e)=>handleInputChange(e)}></input>

            <input type='text' name='duration' value={input.duration} onChange={(e)=>handleInputChange(e)}></input>

            <input type='text' name='season' value={input.season} onChange={(e)=>handleInputChange(e)}></input>

            <button type='submit'>Submit</button>
        </form>
    </div>
  )
};
